/* PAGE */
.profile-page {
  background: #f1f3f6;
  min-height: 100vh;
  padding: 20px 0;
}

.profile-layout {
  max-width: 1200px;
  margin: auto;
  display: grid;
  grid-template-columns: 240px 1fr;
  gap: 12px;
  padding: 0 20px;
}

/* SIDEBAR */
.profile-sidebar {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

/* CARDS */
.card {
  background: #fff;
  border-radius: 2px;
  box-shadow: 0 1px 2px rgba(0,0,0,0.1);
}

.clickable {
  cursor: pointer;
}

.clickable:hover {
  background: #f5f5f5;
}

/* USER CARD */
.user-card {
  padding: 24px 16px;
}

.user-info {
  display: flex;
  align-items: center;
  gap: 12px;
}

.avatar {
  width: 48px;
  height: 48px;
  background: #2874f0;
  color: #fff;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 20px;
  font-weight: 500;
}

.hello {
  font-size: 12px;
  color: #878787;
}

.username {
  font-size: 14px;
  font-weight: 600;
}

/* MENU */
.menu-row {
  padding: 16px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: #878787;
  font-size: 13px;
  font-weight: 500;
}

.menu-left {
  display: flex;
  align-items: center;
  gap: 12px;
}

.menu-group {
  padding: 16px;
}

.menu-title {
  font-size: 11px;
  color: #878787;
  font-weight: 600;
  letter-spacing: 0.5px;
  margin-bottom: 12px;
}

/* SIDEBAR ITEM */
.sidebar-item {
  display: flex;
  align-items: center;
  gap: 16px;
  font-size: 13px;
  color: #878787;
  padding: 10px 0;
  cursor: pointer;
}

.sidebar-item:hover {
  color: #212121;
  font-weight: 500;
}

.sidebar-item.active {
  color: #2874f0;
  font-weight: 600;
}

/* CONTENT */
.profile-content {
  background: #fff;
  border-radius: 2px;
  box-shadow: 0 1px 2px rgba(0,0,0,0.1);
}

.section-padding {
  padding: 30px;
}

.empty-state {
  padding: 60px 30px;
  text-align: center;
  color: #878787;
}
